function addon:GetClassWeights()
    -- Prefer user SavedVariables, fallback to default
    if JamboBagsDB and JamboBagsDB.customWeights and JamboBagsDB.customWeights[addon.MyClass] then
        return JamboBagsDB.customWeights[addon.MyClass]
    end
    return addon.DefaultSpecs[addon.MyClass] or {}
end